CREATE USER 'user'@'localhost' IDENTIFIED BY 'user';
CREATE USER 'user'@'%' IDENTIFIED BY 'user';

GRANT ALL ON *.* TO 'user'@'localhost';
GRANT ALL ON *.* TO 'user'@'%';

CREATE DATABASE textbook;

USE textbook;

-- -----------------------------------------------------
-- Table `textbook`.`book`
-- -----------------------------------------------------
CREATE TABLE IF NOT EXISTS `textbook`.`book` (
  `idBooks` INT NOT NULL AUTO_INCREMENT PRIMARY KEY,
  `Title` TEXT NULL DEFAULT NULL,
  `Author` TEXT NULL DEFAULT NULL,
  `ISBN` TEXT NULL DEFAULT NULL,
  `Description` TEXT NULL DEFAULT NULL,
  `Vendor` TEXT NULL DEFAULT NULL,
  `Price` DOUBLE NULL DEFAULT NULL,
  `Type` TEXT NULL DEFAULT NULL,
  `ProductLink` TEXT NULL DEFAULT NULL)
ENGINE = InnoDB
DEFAULT CHARACTER SET = utf8;


